import React from 'react';
import Keyboard from 'react-simple-keyboard';
import 'react-simple-keyboard/build/css/index.css';
import "../css/keyLearn.css"
import {MDBCol, MDBContainer, MDBRow} from "mdbreact";

export default class VirtualKeyboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            input: "",
        };
    }

    onKeyPress = (button) => {
        console.log("Button pressed", button);
    }

    render(){
        let sharedProps = {
            onKeyPress: button => this.onKeyPress(button),
            physicalKeyboardHighlightPress: true,
            physicalKeyboardHighlight: true,
            // This syncs the input of all keyboards
            syncInstanceInputs: true,
        };
        return (
            <MDBRow>
                <MDBCol size={8}>
                    <Keyboard
                        {...sharedProps}
                        baseClass={"stdKeyboard"}
                        layout={{
                            'default': [
                                '` 1 2 3 4 5 6 7 8 9 0 - = {backspace}',
                                '{tab} q w e r t y u i o p [ ] \\',
                                '{capslock} a s d f g h j k l ; \' {enter}',
                                '{shiftleft} z x c v b n m , . / {shiftright}',
                                '{controlleft} {metaleft} {altleft} {space} {altright} {metaright}  {controllright}'
                            ]
                        }}
                        display={{
                            "{escape}": "esc ⎋",
                            "{space}": "________________________Space________________________",
                            "{tab}": "tab ⇥",
                            "{backspace}": "backspace ⌫",
                            "{enter}": "enter ↵",
                            "{capslock}": "caps lock ⇪",
                            "{shiftleft}": "shift ⇧",
                            "{shiftright}": "shift ⇧",
                            "{controlleft}": "ctrl ⌃",
                            "{controllright}": "ctrl ⌃",
                            "{altleft}": "alt ⌥",
                            "{altright}": "alt ⌥",
                            "{metaleft}": "cmd ⌘",
                            "{metaright}": "cmd ⌘"
                        }}
                    />
                </MDBCol>
                <MDBCol size={4}>
                    <MDBRow>
                        <MDBCol>
                            <Keyboard
                                {...sharedProps}
                                baseClass={"StenoKeyboard1"}
                                layout={{
                                    'default': [
                                        '{space}'
                                    ]
                                }}
                                display={{
                                    '{space}': ' '
                                }}
                            />
                        </MDBCol>
                    </MDBRow>
                    <MDBRow center>
                        <MDBCol>
                            <Keyboard
                                {...sharedProps}
                                baseClass={"StenoKeyboard2"}
                                layout={{
                                    'default': [
                                        'q',
                                        'a'
                                    ]
                                }}
                                display={{
                                    'q': 'S',
                                    'a': 'T'
                                }}
                            />
                        </MDBCol>
                    </MDBRow>
                </MDBCol>
            </MDBRow>
        );
    }
}
