import React from 'react';
import VirtualKeyboard from "../components/virtualKeyboard";
import '../css/study.css'
import {MDBCard} from "mdbreact";
import KeyboardEventHandler from 'react-keyboard-event-handler';

export default class KeyLearn extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            highlightBgColor: "#FF0000",
            hintKey: "q",
            targetKey: "S"
        }
    }

    generateKey = ()=>{
        let nextKey = Math.floor(Math.random() * 22 + 1);
        switch (nextKey){
            case 1:
                this.setState({
                    hintKey: "q",
                    targetKey: "S"
                })
                break;
            case 2:
                this.setState({
                    hintKey: "w",
                    targetKey: "K"
                })
                break;
            case 3:
                this.setState({
                    hintKey: "e",
                    targetKey: "R"
                })
                break;
            case 4:
                this.setState({
                    hintKey: "r f",
                    targetKey: "N"
                })
                break;
            case 5:
                this.setState({
                    hintKey: "t",
                    targetKey: "H"
                })
                break;
            case 6:
                this.setState({
                    hintKey: "q",
                    targetKey: "S"
                })
                break;
            case 7:
                this.setState({
                    hintKey: "u",
                    targetKey: "*"
                })
                break;
            case 8:
                this.setState({
                    hintKey: "i",
                    targetKey: "W"
                })
                break;
            case 9:
                this.setState({
                    hintKey: "o l",
                    targetKey: "J"
                })
                break;
            case 10:
                this.setState({
                    hintKey: "q",
                    targetKey: "S"
                })
                break;
            case 11:
                this.setState({
                    hintKey: "[",
                    targetKey: "T"
                })
                break;
            case 12:
                this.setState({
                    hintKey: "a",
                    targetKey: "T"
                })
                break;
            case 13:
                this.setState({
                    hintKey: "s",
                    targetKey: "P"
                })
                break;
            case 14:
                this.setState({
                    hintKey: "d",
                    targetKey: "H"
                })
                break;
            case 15:
                this.setState({
                    hintKey: "g",
                    targetKey: "S"
                })
                break;
            case 16:
                this.setState({
                    hintKey: "j",
                    targetKey: "I"
                })
                break;
            case 17:
                this.setState({
                    hintKey: "k",
                    targetKey: "Y"
                })
                break;
            case 18:
                this.setState({
                    hintKey: ";",
                    targetKey: "G"
                })
                break;
            case 19:
                this.setState({
                    hintKey: "'",
                    targetKey: "K"
                })
                break;
            case 20:
                this.setState({
                    hintKey: "c",
                    targetKey: "U"
                })
                break;
            case 21:
                this.setState({
                    hintKey: "v",
                    targetKey: "O"
                })
                break;

            case 22:
                this.setState({
                    hintKey: "n",
                    targetKey: "E"
                })
                break;
            default:
                break;
        }
    }

    handleKeyDown = (key)=>{
        this.setState({
            highlightBgColor: "#00FF00"
        })
    }

    handleKeyUp = (key)=>{
        this.generateKey();
        this.setState({
            highlightBgColor: "#FF0000"
        })
    }

    render(){
        return (
            <>
                <MDBCard className="card-body" style={{marginBottom: "50px" }}>
                    <p className="targetKey">{this.state.targetKey}</p>
                </MDBCard>
                <VirtualKeyboard
                    highlightBgColor={this.state.highlightBgColor}
                    hintKey={this.state.hintKey}
                />
                <KeyboardEventHandler
                    handleKeys={[this.state.hintKey]}
                    handleEventType="keydown"
                    onKeyEvent={(key, e) =>{
                        this.handleKeyDown(key);
                    }}
                />
                <KeyboardEventHandler
                    handleKeys={[this.state.hintKey]}
                    handleEventType="keyup"
                    onKeyEvent={(key, e) =>{
                        this.handleKeyUp(key);
                    }}
                />
            </>
        );
    }
}
