import React from 'react';
import VirtualKeyboard from "../components/virtualKeyboard";
import '../css/study.css'
import {MDBBtn, MDBCard, MDBCardText, MDBIcon} from "mdbreact";
import KeyboardEventHandler from 'react-keyboard-event-handler';

export default class WordLearn extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            targetWord: "",
            input: "ss"
        }
    }

    componentDidMount() {
        this.generateWord();
    }

    generateWord = ()=>{
        let nextKey = Math.floor(Math.random() * 20 + 1);
        switch (nextKey){
            case 1:
                this.setState({
                    targetWord: "nhà"
                })
                break;
            case 2:
                this.setState({
                    targetWord: "chảy"
                })
                break;
            case 3:
                this.setState({
                    targetWord: "đi"
                })
                break;
            case 4:
                this.setState({
                    targetWord: "trường"
                })
                break;
            case 5:
                this.setState({
                    targetWord: "lái"
                })
                break;
            case 6:
                this.setState({
                    targetWord: "nóng"
                })
                break;
            case 7:
                this.setState({
                    targetWord: "và"
                })
                break;
            case 8:
                this.setState({
                    targetWord: "con"
                })
                break;
            case 9:
                this.setState({
                    targetWord: "lượm"
                })
                break;
            case 10:
                this.setState({
                    targetWord: "thuần"
                })
                break;
            case 11:
                this.setState({
                    targetWord: "bảng"
                })
                break;
            case 12:
                this.setState({
                    targetWord: "luyện"
                })
                break;
            case 13:
                this.setState({
                    targetWord: "phớt"
                })
                break;
            case 14:
                this.setState({
                    targetWord: "huy"
                })
                break;
            case 15:
                this.setState({
                    targetWord: "khuya"
                })
                break;
            case 16:
                this.setState({
                    targetWord: "vắng"
                })
                break;
            case 17:
                this.setState({
                    targetWord: "chích"
                })
                break;
            case 18:
                this.setState({
                    targetWord: "ngoan"
                })
                break;
            case 19:
                this.setState({
                    targetWord: "lành"
                })
                break;
            case 20:
                this.setState({
                    targetWord: "trăng"
                })
                break;
            default:
                break;
        }
    }

    reload = ()=>{
        this.generateWord();
    }

    render(){
        return (
            <>
                <MDBCard color="mdb-color" text="white" className="text-center card-body target">
                    <div className="target-content">
                        <MDBCardText>{this.state.targetWord}</MDBCardText>
                    </div>
                </MDBCard>
                <input
                    type="text"
                    id="input"
                    className="form-control form-control-lg"
                    value={this.state.input}
                />
                <VirtualKeyboard
                    highlightBgColor={this.state.highlightBgColor}
                    hintKey=" "
                />
                <MDBBtn className="stats" gradient="blue" floating tag='a' onClick={()=>{this.reload()}}>
                    <MDBIcon icon='redo-alt' />
                </MDBBtn>
                <KeyboardEventHandler
                    handleKeys={["enter"]}
                    handleEventType="keyup"
                    onKeyEvent={(key, e) =>{
                        console.log("enter");
                    }}
                />
            </>
        );
    }
}
